example.code:
String[] goods = { "coffee", "flour", "tea"};
float[] amounts = { 108.6, 5.8, 8.2 };

FloatDict inventory;

void setup() {
  size(200, 200);
  inventory = new FloatDict(goods, amounts);
  println(inventory);
  if (inventory.hasKey("flour") == true) {
    println("Yes, we have flour."); 
  } else {
    println("Sorry, no flour.");
  }
}
