example.code:
String[] goods = { "coffee", "flour", "tea"};
float[] amounts = { 108.6, 5.8, 8.2 };

FloatDict inventory;

void setup() {
  size(200, 200);
  inventory = new FloatDict(goods, amounts);
  println(inventory);  // Full inventory
  inventory.remove("flour");
  println(inventory);  // Flour is removed from list
}
