example.code:
String[] goods = { "coffee", "flour", "tea"};
float[] amounts = { 108.6, 5.8, 8.2 };

FloatDict inventory;

void setup() {
  size(200, 200);
  inventory = new FloatDict(goods, amounts);
  println(inventory);  // There is 5.8 of flour
  inventory.sub("flour", 3.4);
  println(inventory);  // There is 2.4 of flour
}
