example.code:
String[] goods = { "cds", "tapes", "records"};
int[] amounts = { 84, 15, 102 };

IntDict inventory;

void setup() {
  size(200, 200);
  inventory = new IntDict(goods, amounts);
  println(inventory);
  if (inventory.hasKey("records") == true) {
    println("Yes, we have records."); 
  } else {
    println("Sorry, no records.");
  }
}
