example.code:
String[] goods = { "cds", "tapes", "records"};
int[] amounts = { 84, 15, 102 };

IntDict inventory;

void setup() {
  size(200, 200);
  inventory = new IntDict(goods, amounts);
  println(inventory);  // There are 15 tapes
  inventory.increment("tapes");
  println(inventory);  // There are now 16 tapes
}
