example.code:
String[] goods = { "cds", "tapes", "records"};
int[] amounts = { 84, 15, 102 };

IntDict inventory;

void setup() {
  size(200, 200);
  inventory = new IntDict(goods, amounts);
  println(inventory);  // Full inventory
  inventory.remove("tapes");
  println(inventory);  // Tapes are removed from list
}
