example.code:
JSONArray json;

void setup() {

  json = new JSONArray();

  json.append(32);
  json.append(1.13);
  json.append("grape");
  json.append(true);

  JSONObject obj = new JSONObject();
  obj.setFloat("persistence", 0.95);
  json.append(obj);

  JSONArray arr = new JSONArray();
  arr.append("red");
  arr.append("green");
  arr.append("blue");
  json.append(arr);

  println(json);
}

// Sketch prints:
// [
//   32,
//   1.1299999952316284,
//   "grape",
//   true,
//   {"persistence": 0.949999988079071},
//   [
//     "red",
//     "green",
//     "blue"
//   ]
// ]
