example.code:
String[] goods = { "coffee", "flour", "tea"};
String[] amounts = { "black", "white", "green" };

StringDict inventory;

void setup() {
  size(200, 200);
  inventory = new StringDict(goods, amounts);
  println(inventory);noLoop();
  if (inventory.hasKey("tea") == true) {
    println("Yes, we have tea."); 
  } else {
    println("Sorry, no tea.");
  }
}
