example.code:
String[] goods = { "coffee", "flour", "tea"};
String[] amounts = { "black", "white", "green" };

StringDict inventory;

void setup() {
  size(200, 200);
  inventory = new StringDict(goods, amounts);
  println(inventory);
  inventory.set("flour", "unbleached");
  inventory.set("cinnamon", "brown");
  println(inventory);
}
