example.code:
String[] goods = { "coffee", "flour", "tea"};
StringList inventory;

void setup() {
  size(200, 200);
  inventory = new StringList(goods);
  println(inventory);
  if (inventory.hasValue("tea") == true) {
    println("Yes, we have a tea"); 
  } else {
    println("Sorry, no tea");
  }
}
